/* eslint react-hooks/exhaustive-deps: off*/
import React, { useEffect, useState } from "react";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";
import GoogleMapReact from 'google-map-react';
// import {ColorfulMessage} from "./components/ColorfulMessage"; 

export const App2 = () => {

  const [num, setNum] = useState(0); //numは変数、setNumは更新関数
  const [ahoahoShowFlag, setAhoahoShowFrag] = useState(false);
  const [map, setMap] = useState(null);
  const [maps, setMaps] = useState(null);
  const [marker, setMarker] = useState(null);

  const onClickButton = () => {
    setNum(num + 1);
  };

  const onClickSwithShowFlag = () => {
    setAhoahoShowFrag(!ahoahoShowFlag);
  };

  useEffect(() => {
    if (num > 0) {
      if (num % 3 === 0) {
        ahoahoShowFlag || setAhoahoShowFrag(true);
      } else {
        ahoahoShowFlag && setAhoahoShowFrag(false);
      }
    }
  }, [num]);//第二引数に与えた関数に関心を持つ関数となる。

  const containerStyle = {
    width: "100%",
    height: "400px",
  };

  const center = {
    lat: 35.69575,
    lng: 139.77521,
  };

  // const positionAkiba = {
  //   lat: 35.69731,
  //   lng: 139.7747,
  // };

  // const positionIwamotocho = {
  //   lat: 35.69397,
  //   lng: 139.7762,
  // };

  // const markerLabelAkiba = {
  //   color: "white",
  //   fontFamily: "sans-serif",
  //   fontSize: "15px",
  //   fontWeight: "100",
  //   text: "5",
  // };

  // const markerLabelIwamotocho = {
  //   color: "white",
  //   fontFamily: "sans-serif",
  //   fontSize: "15px",
  //   fontWeight: "100",
  //   text: "12",
  // };

  const handleApiLoaded = (object) => {
    setMap(object.map);
    setMaps(object.maps);
  };

  const setLatLng = ({ x, y, lat, lng, event }) => {
    if (marker) {
      marker.setMap(null);
    }
    const latLng = {
      lat,
      lng,
    };
    setMarker(new maps.Marker({
      map,
      position: latLng,
    }));
    map.panTo(latLng);
  };

  return (
    <>

      <GoogleMapReact
        bootstrapURLKeys={{ key: process.env.NEXT_PUBLIC_GOOGLE_MAP_KEY }}
        defaultCenter={center}
        defaultZoom={16}
        onClick={setLatLng}
        onGoogleApiLoaded={handleApiLoaded}
      />
      <h1 style={{ color: "black" }}>Hello World</h1>
      <LoadScript>
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={17}
        >
          <Marker position={positionAkiba} label={markerLabelAkiba} />
          <Marker position={positionIwamotocho} label={markerLabelIwamotocho} />
        </GoogleMap>
      </LoadScript>
      {/* <ColorfulMessage color="black"> お元気ですよね?</ColorfulMessage>  */}
      {/* <ColorfulMessage color="pink"> 元気です</ColorfulMessage> */}
      <button onClick={onClickButton}>カウントアップ</button>
      <button onClick={onClickSwithShowFlag}>on/off</button>
      <p>{num}</p>
      {ahoahoShowFlag && <p>あほあほ</p>}
      <p>あほあほ</p>

    </>
  )

};
